// Tag generation based on word characteristics
function generateTags(word: VocabularyWord): string[] {
  const tags: string[] = []

  // Difficulty-based tags
  tags.push(word.difficulty.toLowerCase())

  // Length-based tags
  if (word.arabic.length <= 3) {
    tags.push("short-word")
  } else if (word.arabic.length <= 8) {
    tags.push("medium-word")
  } else {
    tags.push("long-word")
  }

  // Frequency-based tags (if available)
  if (word.frequency) {
    if (word.frequency > 100) {
      tags.push("high-frequency")
    } else if (word.frequency > 20) {
      tags.push("medium-frequency")
    } else {
      tags.push("low-frequency")
    }
  } else {
    tags.push("low-frequency")
  }

  // Surah-based tags
  if (word.examples && word.examples.length > 0) {
    const surahNumbers = [...new Set(word.examples.map((ex) => ex.surahNumber))]
    surahNumbers.forEach((surahNum) => {
      tags.push(`juz-${surahNum <= 1 ? "1" : "30"}`)
    })
  }

  // Category-based tags
  if (word.category) {
    const categoryTag = word.category
      .toLowerCase()
      .replace(/[^a-z0-9\s]/g, "")
      .replace(/\s+/g, "-")
    tags.push(categoryTag)
  }

  return [...new Set(tags)] // Remove duplicates
}

// Categorize a word based on its Arabic text and meanings
function categorizeWord(word: VocabularyWord): string {
  const arabicText = word.arabic.toLowerCase()
  const meanings = word.meanings.join(" ").toLowerCase()
  const transliteration = word.transliteration.toLowerCase()

  // Check surah-based categories first
  if (word.examples && word.examples.length > 0) {
    const surahNumber = word.examples[0].surahNumber
    if (surahCategories[surahNumber]) {
      return surahCategories[surahNumber]
    }
  }

  // Check pattern-based categories
  for (const rule of categoryRules) {
    for (const pattern of rule.patterns) {
      if (pattern.test(arabicText) || pattern.test(meanings) || pattern.test(transliteration)) {
        return rule.category
      }
    }
  }

  // Default category
  return "General Quranic Vocabulary"
}

async function improveVocabularyCategories() {
  console.log("🔄 Improving vocabulary categories and tags...\n")

  const filePath = path.join(process.cwd(), "data", "vocabulary-data-quiz-extracted.ts")

  // Check if file exists
  if (!fs.existsSync(filePath)) {
    console.log("❌ Quiz-extracted vocabulary file not found!")
    console.log("   Please run the extraction script first.")
    return
  }

  // Read the file
  const fileContent = fs.readFileSync(filePath, "utf-8")

  // Extract the vocabulary array using regex
  const arrayMatch = fileContent.match(/export const quizExtractedVocabulary: VocabularyWord\[\] = (\[[\s\S]*?\]);/)
  if (!arrayMatch) {
    console.log("❌ Could not parse vocabulary data from file!")
    return
  }

  // Parse the vocabulary data
  let vocabularyData: VocabularyWord[]
  try {
    vocabularyData = eval(arrayMatch[1])
  } catch (error) {
    console.log("❌ Error parsing vocabulary data:", error)
    return
  }

  console.log(`📊 Processing ${vocabularyData.length} words...\n`)

  // Improve each word
  const improvedVocabulary = vocabularyData.map((word) => {
    const improvedWord = { ...word }

    // Assign category
    improvedWord.category = categorizeWord(word)

    // Generate tags
    improvedWord.tags = generateTags(improvedWord)

    return improvedWord
  })

  // Generate statistics
  const categoryStats: { [key: string]: number } = {}
  improvedVocabulary.forEach((word) => {
    const category = word.category || "Uncategorized"
    categoryStats[category] = (categoryStats[category] || 0) + 1
  })

  // Sort categories by count
  const sortedCategories = Object.entries(categoryStats).sort(([, a], [, b]) => b - a)

  console.log("✅ Categories and tags improved successfully!\n")
  console.log("📈 Category Distribution:")
  sortedCategories.forEach(([category, count]) => {
    console.log(`   - ${category}: ${count} words`)
  })

  // Generate the updated file content
  const updatedContent = `import type { VocabularyWord } from "../types/vocabulary"

export const quizExtractedVocabulary: VocabularyWord[] = ${JSON.stringify(improvedVocabulary, null, 2)}
`

  // Write the updated file
  fs.writeFileSync(filePath, updatedContent, "utf-8")
  console.log(`\n📁 Updated file: ${filePath}`)

  // Show sample improvements
  console.log("\n🔍 Sample improvements:\n")
  improvedVocabulary.slice(0, 5).forEach((word, index) => {
    console.log(`   ${index + 1}. ${word.arabic} (${word.transliteration})`)
    console.log(`      Category: ${word.category}`)
    console.log(`      Tags: ${word.tags?.slice(0, 5).join(", ")}${word.tags && word.tags.length > 5 ? "..." : ""}`)
    console.log()
  })

  console.log("📝 Next steps:")
  console.log("   1. Review the categorization and make manual adjustments if needed")
  console.log("   2. Deploy the updated vocabulary to see improved organization")
  console.log("   3. The enhanced vocabulary service will automatically use these categories")
}

// Run the improvement function
improveVocabularyCategories().catch(console.error)
